      "author": "Hayden Carruth",
      "author_id": 0,
      "author_bio": "",
      "authors": "Hayden Carruth, Susan Kagen Podell",
      "title_slug": "the-voice-that-is-great-within-us",
      "author_slug": "hayden-carruth",
      "isbn13": 9780553262636,
      "isbn10": "0553262637",
